
DROP TABLE IF EXISTS `gProcess`;
CREATE TABLE gProcess (entity int not null AUTO_INCREMENT ,owner char(255),process char(255),stepName char(255),stepTitle char(255),activityNum int ,stepNum int ,subStepNum int ,text text,img text,attention char(255),timerName char(255),timerTime int ,fixed_boil_off float ,fixed_cool_off float ,percentage_boil_off float ,percentage_cool_off float ,auto char(255),needToComplete int ,compileStep int ,conditional text, PRIMARY KEY(entity) );
