DROP TABLE IF EXISTS `gPurchases`;
CREATE TABLE gPurchases (entity int not null AUTO_INCREMENT, owner char(255), storecategory char(128), storeitem char(128), itemcategory char(128), itemsubcategory char(128), purchaseQty float, qty float, originalqty float, qtyMultiple float, wastageFixed float, purchaseDate int, bestBeforeEnd int, supplier char(128), purchaseCost float, stocktag char(128), hopActualAlpha float, unit char(128), volume float,willNotExpire int, PRIMARY KEY(entity));


